import React, {useState} from "react";

function Abc() {
    const [inputValue, setInputValue]=useState("");
    const handleSubmit = (e)=>{
        e.preventDefault();
        alert("Gia tri ban nhap la: "+inputValue);
    };
    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                placeholder="Nhap gi do"
                value={inputValue}
                onChange={(e)=>setInputValue(e.ta)} 
            />

        </form>
    );
}
export default Abc;
